<?php

namespace SharengoCore\Entity\Repository;

use Doctrine\ORM\Query\ResultSetMapping;

/**
 * CarsRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CarsRepository extends \Doctrine\ORM\EntityRepository
{
    public function getTotalCars()
    {
        $em = $this->getEntityManager();
        $query = $em->createQuery('SELECT COUNT(c.plate) FROM \SharengoCore\Entity\Cars c');
        return $query->getSingleScalarResult();
    }

    public function findCarsEligibleForAlarmCheck()
    {
        $em = $this->getEntityManager();

        $dql = "SELECT c
        FROM \SharengoCore\Entity\Cars c
        WHERE NOT EXISTS
        (SELECT 1
        FROM \SharengoCore\Entity\Trips t
        WHERE t.car = c
        AND t.timestampEnd is null)";

        $query = $em->createQuery($dql);

        return $query->getResult();
    }

    public function findPublicCars()
    {
        $em = $this->getEntityManager();

        $dql = "SELECT c
        FROM \SharengoCore\Entity\Cars c
        WHERE c.status = 'operative'
        AND c.active = true
        AND c.hidden = false
        AND c.longitude != 0
        AND c.latitude != 0";

        $query = $em->createQuery($dql);

        return $query->getResult();
    }

    public function findPublicFreeCarsByFleet($fleet)
    {
        $em = $this->getEntityManager();

        $dql = "SELECT c
        FROM \SharengoCore\Entity\Cars c
        LEFT JOIN c.fleet f
        WHERE c.status = 'operative'
        AND c NOT IN (
            SELECT DISTINCT c1
            FROM \SharengoCore\Entity\Trips t
            JOIN t.car c1
            WHERE t.timestampEnd IS NULL
        )
        AND c NOT IN (
            SELECT DISTINCT c2
            FROM \SharengoCore\Entity\Reservations r
            JOIN r.car c2
            WHERE r.active = true
        )
        AND c.active = true
        AND c.hidden = false
        AND c.longitude != 0
        AND c.latitude != 0
        AND f = :fleet";

        $query = $em->createQuery($dql);
        $query->setParameter('fleet', $fleet);

        return $query->getResult();
    }

    /**
     * @param Cars
     * @return bool whether the car is or not inside the zones associated to its fleet
     */
    public function checkCarInFleetZones($car)
    {
        $em = $this->getEntityManager();

        $sql = "SELECT coalesce(bool_or(za.geo @> point(c.longitude, c.latitude)), false) AS is_in
            FROM cars c
            JOIN fleets f ON f.id = c.fleet_id
            JOIN zone_alarms_fleets zaf ON zaf.fleet_id = f.id
            JOIN zone_alarms za ON za.id = zaf.zone_alarm_id AND za.active = TRUE
            WHERE c.plate = :plate";

        $rsm = new ResultSetMapping;
        $rsm->addScalarResult('is_in', 'isIn', 'boolean');

        $query = $em->createNativeQuery($sql, $rsm);
        $query->setParameter('plate', $car->getPlate());

        return $query->getSingleScalarResult();
    }

    public function findReservedPlates()
    {
        $em = $this->getEntityManager();

        $sql = "SELECT json_agg(plate) as value
            FROM (
                SELECT DISTINCT c.plate as plate
                FROM cars c
                JOIN reservations r ON r.car_plate = c.plate AND r.active = true
                WHERE r.length != -1
                ORDER BY c.plate ASC
            ) sub_q";

        $rsm = new ResultSetMapping;
        $rsm->addScalarResult('value', 'value', 'json_array');

        $query = $em->createNativeQuery($sql, $rsm);

        return $query->getResult();
    }

    public function findBusyPlates()
    {
        $em = $this->getEntityManager();

        $sql = "SELECT json_agg(plate) as value
            FROM (
                SELECT DISTINCT c.plate as plate
                FROM cars c
                JOIN trips t ON t.car_plate = c.plate AND t.timestamp_end IS NULL
                ORDER BY c.plate ASC
            ) sub_q";

        $rsm = new ResultSetMapping;
        $rsm->addScalarResult('value', 'value', 'json_array');

        $query = $em->createNativeQuery($sql, $rsm);

        return $query->getResult();
    }

    /**
     * The outer query aggregates the results of the subquery, generating json.
     * The subquery selects the plate of the car and the highest timestamp of
     * the relative trips. It then subtracts that timestamp from the current
     * time and extracts the seconds from the result. It then converts the
     * result to minutes and rounds it up.
     */
    public function findMinutesSinceLastTrip()
    {
        $em = $this->getEntityManager();

        $sql = "SELECT json_object_agg(plate, ts_end) as value
            FROM (
                SELECT
                    c.plate as plate,
                    round(extract('epoch' from (now() - MAX(t.timestamp_end))) / 60) as ts_end
                FROM trips t
                JOIN cars c ON t.car_plate = c.plate
                WHERE t.timestamp_end IS NOT NULL AND
                t.customer_id NOT IN (SELECT id FROM customers WHERE maintainer = TRUE GROUP BY id)
                GROUP BY c.plate
                ORDER BY c.plate ASC
            ) sub_q";

        $rsm = new ResultSetMapping;
        $rsm->addScalarResult('value', 'value', 'json_array');

        $query = $em->createNativeQuery($sql, $rsm);

        return $query->getResult();
    }

    /**
     * The outer query aggregates the results of the subquery, generating json.
     * The subquery selects the plate of the car and a boolean value
     * corresponding to wether the car is out of its permitted boundaries.
     * The boolean value might also be null if longitude or latitude are null.
     */
    public function findOutOfBoundsPlates()
    {
        $em = $this->getEntityManager();

        $sql = "SELECT json_object_agg(plate, gps) as value
            FROM (
                SELECT
                    c.plate as plate,
                    bool_or(za.geo @> point(c.longitude, c.latitude)) as gps
                FROM cars c
                JOIN fleets f ON f.id = c.fleet_id
                JOIN zone_alarms_fleets zaf ON zaf.fleet_id = f.id
                JOIN zone_alarms za ON za.id = zaf.zone_alarm_id AND za.active = TRUE
                GROUP BY c.plate
                ORDER BY c.plate ASC
            ) sub_q";

        $rsm = new ResultSetMapping;
        $rsm->addScalarResult('value', 'value', 'json_array');

        $query = $em->createNativeQuery($sql, $rsm);

        return $query->getResult();
    }

    public function getPublicCarsForAddFreeX($fleet_id, $date) {
        $em = $this->getEntityManager();

        $dql = "SELECT c
        FROM \SharengoCore\Entity\Cars c 
        WHERE c.status = 'operative' 
        AND c.active = true 
        AND c.hidden = false 
        AND c.fleet = :fleet 
        AND c.longitude != 0 
        AND c.latitude != 0 
        AND c NOT IN (
            SELECT DISTINCT c1
            FROM \SharengoCore\Entity\Trips t
            JOIN t.car c1
            WHERE t.timestampEnd IS NULL
            OR t.timestampEnd > :date
        )
        ";
        $query = $em->createQuery($dql);
        $query->setParameter('fleet', $fleet_id);
        $query->setParameter('date', $date);

        return $query->getResult();
    }

}
